mapper.select:
select * from t_user where 1=1

    and userName like "%${userBean.userName}%"

limit #{userBean.startIndex},#{userBean.rows}

mapper.update:
	update t_user
  set userName = #{userName},loginNumber = #{loginNumber},password = #{password}
  where userId = #{userId}

	update t_user
  set userName = #{userName},loginNumber = #{loginNumber},password = #{password},
  sex=#{sex},phone=#{phone},birthday=#{birthday},province=#{province},city=#{city},
  area=#{area},email=#{email},userInfo=#{userInfo},userImg=#{userImg}
  where userId = #{userId}
